import React, { useEffect, useRef } from 'react';
import bootstrap from 'bootstrap'; // Import the Bootstrap package

const MyComponent: React.FC = () => {
  const popoverRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (popoverRef.current) {
      // Initialize the Bootstrap Popover when the component mounts
      const popover = new bootstrap.Popover(popoverRef.current, {
        trigger: 'focus'
      });

      return () => {
        // Destroy the Bootstrap Popover when the component unmounts
        popover.dispose();
      };
    }
  }, []);
    return(
        <div className="col-sm-6 col-md-4 col-lg-3">
            <a className="book-container">
                <img src="https://m.media-amazon.com/images/M/MV5BODRmZDVmNzUtZDA4ZC00NjhkLWI2M2UtN2M0ZDIzNDcxYThjL2ltYWdlXkEyXkFqcGdeQXVyNTk0MzMzODA@._V1_FMjpg_UX1000_.jpg" alt="kiminonawa" className="book-image" />
            </a>
            <a tabIndex={2} className="btn btn-lg btn-danger" role="button" data-bs-toggle="popover" data-bs-trigger="focus" data-bs-title="Dismissible popover" data-bs-content="And here's some amazing content. It's very engaging. Right?">Dismissible popover</a>    
        </div>
    );
}
